import UIKit

protocol DetailPresentationLogic {
    func presentUrl(response: Detail.WebView.Response)
}

class DetailPresenter: DetailPresentationLogic {
    weak var viewController: DetailDisplayLogic?

    // MARK: Do something

    func presentUrl(response: Detail.WebView.Response) {
        let viewModel = Detail.WebView.ViewModel(url: response.url)
        viewController?.displayWeb(viewModel: viewModel)
    }
}
