import UIKit

@objc protocol HomeRoutingLogic {
    func routeToDetail()
}

protocol HomeDataPassing {
    var dataStore: HomeDataStore? { get }
}

class HomeRouter: NSObject, HomeRoutingLogic, HomeDataPassing {
    weak var viewController: HomeViewController?
    var dataStore: HomeDataStore?

    // MARK: Routing

    func routeToDetail() {
        let storyboard = UIStoryboard(name: "Main", bundle: nil)
        let destinationVC = storyboard.instantiateViewController(withIdentifier: "DetailViewController") as! DetailViewController
        var destinationDS = destinationVC.router!.dataStore!
        passDataToDetail(source: dataStore!, destination: &destinationDS)
        navigateToSomewhere(source: viewController!, destination: destinationVC)
    }

     // MARK: Navigation

    func navigateToSomewhere(source: HomeViewController, destination: DetailViewController) {
      source.show(destination, sender: nil)
    }

     // MARK: Passing data
    func passDataToDetail(source: HomeDataStore, destination: inout DetailDataStore) {
        destination.url = source.selectedNews
    }
}
